recommend_begin_time=#{hzn_recommend_begin_time},
recommend_end_time=#{hzn_recommend_end_time}
WHERE recruiting_id=#{recruiting_id}

UPDATE zx_recruitment_information
SET whether_on_homepage_recommend='否'
WHERE recruiting_id=#{recruiting_id}
