mapper.select:
SELECT * FROM zx_merchants_approve a left join zx_employee_basic_information b ON a.audit_staff=b.employees_id
1=1
AND status=#{hzn_approval_status}
AND CONCAT_WS(',',companyName,applyTime,industryInvolved,head,phone,status,auditTime,auditStaff) LIKE '%${hzn_search_content}%'
limit #{offset},#{rows}

mapper.update:
update zx_merchants_account set merchants_name=#{company_name},merchants_level='普通',head=#{head},address=#{company_address},longitude=#{longitude},latitude=#{latitude},
job=#{job},phone=#{phone},url=#{url},merchants_state='正常'
WHERE user_id=#{user_id}
